let fs = require('fs');
let fileUtil = {
    getImageFileList: function(directory, cb)
    {
        fs.readdir(directory, function(err, matches)
        {
            if (err)
            {
                cb(err, null);
                return;
            }
            let fileList = [];
            matches.filter(function(file)
            {
                return /.*\.(jpg|png|gif)$/.test(file); // 絞り込み
            }).forEach(function(file)
            {
                fileList.push(directory + '/' + file);
            });
            cb(null, fileList);
        });
    },
};

module.exports = fileUtil;
