
window.onload = function()
{
    const mainController = require('main/index');

    window.document.onkeydown = function(evt)
    {
        let kc;
        if (evt)
        {
            kc = evt.keyCode;
        }
        else
        {
            kc = event.keyCode;
        }
        const chr = String.fromCharCode(kc);
        console.log(chr);

        if (chr === 'J')
        {
            mainController.down();
        }
        if (chr === 'K')
        {
            mainController.up();
        }
        if (chr === 'L')
        {
            mainController.right();
        }
        if (chr === 'H')
        {
            mainController.left();
        }
        if (chr === 'P')
        {
            mainController.play();
        }
        if (chr === 'F')
        {
            mainController.fullSize();
        }
        if (chr === 'D')
        {
            mainController.defaultSize();
        }
    };
};