var mainImage = require('main/index');

window.onload = function() {
    window.document.onkeydown = function(evt) {
        let kc;
        if (evt) {
            kc = evt.keyCode;
        } else {
            kc = event.keyCode;
        }
        var chr = String.fromCharCode(kc);

        if (chr == 'J') {
            mainImage.down();
        }
        if (chr == 'K') {
            mainImage.up();
        }
        if (chr == 'L') {
            mainImage.right();
        }
        if (chr == 'H') {
            mainImage.left();
        }
        if (chr == 'F') {
            mainImage.fullSize();
        }
        if (chr == 'D') {
            mainImage.defaultSize();
        }
    }
}